
package arjunsucks;
import java.util.*;
public class inttobyte {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int i=sc.nextInt();
		byte iinbyte=(byte)i;
		double d=sc.nextDouble();
		byte dinbyte=(byte)d;
		System.out.println("i = "+i+" b = "+iinbyte);
		System.out.println("d = "+d+" b = "+dinbyte);
	}

}
